import { useState, useEffect } from "react";
import { supabase } from "../supabaseClient";
import { Link } from "react-router-dom"

export default function GetJobs({ userID }) {
    const [jobs, setJobs] = useState([]);

    useEffect(() => {
        const getJobsList = async () => {
            let { data, error } = await supabase
            .from('job')
            .select('id, name, hourlyRate')
            .eq('userFK', userID)
            if (error) console.log(error)
            else setJobs(data);
        }

        getJobsList();
    }, [userID]);

    return (
        <div className="container p-4 my-4 border">
            <p>Your Jobs</p>
            <div class="row my-4">
                <div class="col-4"></div>
                <div class="col-4"></div>
                <div class="col-4"><button type="button" class="btn btn-success float-end">+ New Job</button></div>
            </div>

            <div>
            {jobs.length > 0 ?
                jobs.map((job) => (
                    <Link className="btn btn-success mx-1" to={`/viewjob/${job.id}`}>{job.name}</Link>
                )) : <p>add a job</p>
            }
            </div>
        </div>
    )
}
